function gradient(c,b){var a=document.getElementById(c);a.style.opacity=b;a.style.MozOpacity=b;a.style.KhtmlOpacity=b;a.style.filter="alpha(opacity="+b*100+")";a.style.display="block";return}function fadein(b){var a=0;while(a<=1){setTimeout("gradient('"+b+"',"+a+")",(a*1000)+10);a+=0.01}}function openbox1(b,d){var c=document.getElementById("box1");document.getElementById("filter1").style.display="block";document.getElementById("filter1").style.height=getPageHeight()+"px";var a=document.getElementById("boxtitle1");a.innerHTML=b;if(d){gradient("box1",0);fadein("box1")}else{c.style.display="block"}}function closebox1(){document.getElementById("box1").style.display="none";document.getElementById("filter1").style.display="none"}function getPageHeight(){var c,a;if(window.innerHeight&&window.scrollMaxY){c=window.innerWidth+window.scrollMaxX;a=window.innerHeight+window.scrollMaxY}else{if(document.body.scrollHeight>document.body.offsetHeight){c=document.body.scrollWidth;a=document.body.scrollHeight}else{c=document.body.offsetWidth;a=document.body.offsetHeight}}var b,d;if(self.innerHeight){if(document.documentElement.clientWidth){b=document.documentElement.clientWidth}else{b=self.innerWidth}d=self.innerHeight}else{if(document.documentElement&&document.documentElement.clientHeight){b=document.documentElement.clientWidth;d=document.documentElement.clientHeight}else{if(document.body){b=document.body.clientWidth;d=document.body.clientHeight}}}if(a<d){pageHeight=d}else{pageHeight=a}if(c<b){pageWidth=c}else{pageWidth=b}return pageHeight};